// Template for comparison
const template = {
    "question1": "Y",
    "question2": "Y",
    "question3": "N",
    "hook_cracks": "N",
    "hook_throat": "N",
    "hook_twisted": "N",
    "question5": "Y",
    "question5a": "N",
    "question5b": "N",
    "question6": "Y",
    "question6a": "N",
    "question6b": "N",
    "question6c": "N",
    "question7": "Y",
    "question7a": "N",
    "question7b": "N",
    "question7c": "N",
    "question8": "N"
};

// Initialize result object
let result = {
    matchesTemplate: true,
    mismatches: []
};

// Check each question and condition
function checkQuestion(question, expectedValue) {
    if (msg.payload[question] !== expectedValue) {
        result.matchesTemplate = false;
        result.mismatches.push({
            question: question,
            expected: expectedValue,
            received: msg.payload[question]
        });
    }
}

// Compare each item in the template
for (let question in template) {
    if (template.hasOwnProperty(question)) {
        checkQuestion(question, template[question]);
    }
}

// Output the result
if (result){
    return [null, result];
} else {
    return [msg, null]
}

